const Main = () => {

    const container = {
        border: '1px solid black',
        textAlign: 'center',
        width: '100%',
        height: '95vh',
        margin: '0px,',
        padding: '0px',
        backgroundImage: 'url("pokemon-bg.jpeg")'
    }

    const content = {
        fontFamily: 'sans-serif',
        border: '1px solid black',
        textAlign: 'center',
        backgroundColor: 'black',
        color: 'white'
    }

    const h1 = {
        fontFamily: 'sans-serif',
        // border: '1px solid black',
        // textAlign: 'center',
        // backgroundColor: 'black',
        color: 'white'
    }

    const h4 = {
        fontFamily: 'sans-serif',
        // border: '1px solid black',
        // textAlign: 'center',
        // backgroundColor: 'black',
        color: 'black',
        textAlign: 'center'
      }

    const nav = {
        textAlign: 'center',
    }

    const pDiv = {
        fontFamily: 'sans-serif',
        backgroundColor: 'white',
        width: '500px',
        height: '400px',
        textAlign: 'center',
        display: 'inline-block',
        border: '1px solid #141414',
        boxShadow: '5px 5px 30px #141414'
    }

    return(
        <>
        <div style={container}>
            <div style={content}>
                <h1 style={h1}>Welcome to the Pokemon App</h1>
            </div>
            <nav style={nav}>
                <h2>Links</h2>
                <h2>
                <a href="/" alt="">Home</a> | <a href="/pokemon" alt="List Pokemon">List Pokemon</a> | <a href="/pokemon/new" alt="Add Pokemon">Create Pokemon</a>
                </h2>
            </nav>
            <div style={pDiv}><h2>Create your own Pokemon and BATTLE!</h2></div>
        </div>
        <h4 style={h4}>Copyright &copy; 2023. Pika Pika. All Rights Reserved.</h4>
        </>
    )
}



export default Main;
