import React from 'react'

const Show = ({pokemon, pokedex}) => {


  const container = {
    border: '1px solid black',
    textAlign: 'center',
    width: '100%',
    height: '98vh',
    margin: '0px,',
    padding: '0px',
    backgroundImage: 'url("../pokemon-bg.jpeg")'
}

const content = {
  fontFamily: 'sans-serif',
  border: '1px solid black',
  textAlign: 'center',
  backgroundColor: 'black',
  color: 'white'
}

const h1 = {
  fontFamily: 'sans-serif',
  // border: '1px solid black',
  // textAlign: 'center',
  // backgroundColor: 'black',
  color: 'white'
}

const nav = {
  textAlign: 'center',
}

    const myStyle = {
        fontFamily: 'sans-serif',
        color: '#fff',
        backgroundColor: '#000',
        textAlign: 'center',
        padding: '10px'
      };


      const img = {
        border: '25px solid hotpink',
        backgroundColor: 'white',
        margin: '10px',
        borderRadius: '25px',
        padding: '20px'
      }


    return (
        <>
            <div style={container}>
                <div style={content}>
                    <h1 style={h1}>Gotta Catch 'Em All!</h1>
                </div>

                <nav style={nav}>
                    <h2>
                      <a href="/" alt="">Home</a> | <a href="/pokemon" alt="List Pokemon">List Pokemon</a> | <a href="/pokemon/new" alt="Add Pokemon">Create Pokemon</a>
                    </h2>
                </nav>
                <h2>{pokemon.name[0].toUpperCase() + pokemon.name.slice(1)}</h2>
                <img style={img} src={pokemon.img + '.jpg'} alt={pokemon.name} />
                <h2>{pokemon.readyToFight ? 'Ready to FIGHT!' : 'Cowards away...!'}</h2>
                <h2><a href={`/pokemon`}> Go Back </a></h2>
            </div>
        </>
    )
}

export default Show

